import React, { Component } from "react";
import './secondpage.css';
import Poster from './poster.js'
import PosterSample from './postersamples.js'
import exportedposters from './App.js'

import doggo from './img/doggo.jpg';

// import {Rectangle, Circle, Ellipse, Line, Polyline, CornerBox, Triangle} from 'react-shapes';

const ii1 = require('./img/sample1.png')
const ii2 = require('./img/sample2.png')
const ii3 = require('./img/sample3.png')
const ii4 = require('./img/sample4.png')
const ii5 = require('./img/sample5.png')
const ii6 = require('./img/sample6.png')
const ii7 = require('./img/sample7.png')
const ii8 = require('./img/sample8.png')
const ii9 = require('./img/sample9.png')
const ii10 = require('./img/sample10.png')

// secondpage will read in the posters, the currently selected one needs to be 
// read by the poster component and passed to the parent\\

// listdata from child is the information gotten from the child

class SecondPage extends Component {
  constructor(props) {
  	super(props)

  	this.state = {
  		parray: [],
  		clickedposter: ii2,
  	}

  	this.handleSelection = this.handleSelection.bind(this)
  }

// sets up the posters that are displayed
  componentDidMount() {
    this.setState(prevState => ({
      parray: [...prevState.parray, ii1, ii2, ii3, ii4, ii5, ii6, ii7, ii8, ii9, ii10]

    }))

  }

// once you click a poster, this function handles the selection, setting the clicked poster image to the one that was clicked
  handleSelection(data) {
  	this.setState({
      clickedposter: data
    });


  }

  render() {
    return (

      <div className = 'lrcontainer2'>


	      <div className = 'leftside2'>

	      	<div className='buttflexrow'>
		      	<button className='button2' onClick={()=>{this.props.history.push('/');}} > Back </button>
		      	<div className='instructions'>  </div>
		      	<button className='button2' onClick={() => {this.props.history.push('/mergepage');}}  >  Transition </button>
	      	</div>


	      	<div className = 'row'>
	      		<Poster importedposter = {ii1} handleSelection={this.handleSelection}  />

	      	</div>



	      </div>






	      <div className = 'rightside2'>

		      <div className='sideflex'> 

			      <div className='simple'> Simple </div>

			      <div>

			      	<div className = 'topflex'>


				      	<div> Disorder</div> 
				      	<div> Sharp </div> 
				      	<div> Bright </div> 
			      	</div>

			      	<div className = 'row'>
			      		<span className="square2"></span>
			      		<span className="square3" id='null'></span>
			      		<span className="square2"></span>
			      		<span className="square3" id='null'></span>
			      		<span className="square2"></span>
			      	</div>


			      	<div className = 'row'>
			      		<span className="square3"></span>
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      		<span className="square3"></span>
			      	</div>

			      	<div className = 'row'>
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      		<PosterSample posterselection={this.state.clickedposter} />
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      	</div>

			      	<div className = 'row'>
			      		<span className="square3"></span>
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      		<span className="square2"></span>
			      		<span className="square3"></span>
			      	</div>

			      	<div className = 'row'>
			      		<span className="square2"></span>
			      		<span className="square3"></span>
			      		<span className="square2"></span>
			      		<span className="square3"></span>
			      		<span className="square2"></span>
			      	</div>

			      	<div className = 'topflex'>
				      	<div> Dark </div> 
				      	<div> Soft </div> 
				      	<div> In order </div> 
			      	</div>


			      </div>

			      <div className='complex'> Complex </div>

		      </div>

	      </div>

      </div>


    );
  }
}

export default SecondPage;
