import React, { Component } from "react";

import {
  Route,
  NavLink,
  HashRouter
} from "react-router-dom";

import App from "./App.js";
import SecondPage from "./secondpage.js";

class Main extends Component {
  render() {
    return (
      <HashRouter>

        <div>
          <h1>Simple SPA</h1>
          <ul className="header">
            <li><NavLink to="/">Home</NavLink></li>
            <li><NavLink to="/secondpage">SecondPage</NavLink></li>

          </ul>
          <div className="content">

          </div>
        </div>

      </HashRouter> 
      // provides the foundation for navigation and browser history
    );
  }
}

export default Main;
