import React from 'react';
import {Route, withRouter} from 'react-router-dom'
import  PropTypes  from 'prop-types'

import doggo from './img/doggo.jpg';
import postersample from './img/poster1.jpg'
import './poster.css';


import { createHashHistory } from 'history'



class Poster extends React.Component{

	constructor(props) {
		super(props)
		this.handlePosterClick = this.handlePosterClick.bind(this)

	}

	handlePosterClick() {
		console.log(this.props.location.pathname)
		if(this.props.location.pathname === '/') {
			this.props.history.push('/secondpage');
		} else {
			this.props.history.push('/mergepage');

		}


	}

	static contextTypes = {
		router: PropTypes.object
	}


  render(){
    return <img className = 'poster' src = {doggo} onClick={this.handlePosterClick}/>
  }
}





export default withRouter(Poster);
