import React from 'react';
// import logo from './logo.svg';
import './App.css';

// this was used to test the functionality of exporting things from page to page
import doggo from './img/doggo.jpg';

import Poster from './poster.js'
import { createHashHistory } from 'history'

const img=10000000000;


class App extends React.Component{

  constructor(props) {
    super(props);
    this.handleCoolClick = this.handleCoolClick.bind(this)
    this.handleCoolWarmClick = this.handleCoolWarmClick.bind(this)
    this.handleWarmClick = this.handleWarmClick.bind(this)

    this.handleHighClick = this.handleHighClick.bind(this)
    this.handleHighLowClick = this.handleHighLowClick.bind(this)
    this.handleLowClick = this.handleLowClick.bind(this)

    this.handleBrightClick = this.handleBrightClick.bind(this)
    this.handleBrightDarkClick = this.handleBrightDarkClick.bind(this)
    this.handleDarkClick = this.handleDarkClick.bind(this)


    this.state = {
      CoolClicked: false,
      CoolWarmClicked: true,
      WarmClicked: false,

      HighClicked: false,
      HighLowClicked: true,
      LowClicked: false,

      BrightClicked: false,
      BrightDarkClicked: true,
      DarkClicked: false,

      circle: false,
      square: false,
      triangle: false,

    }
  }

  handleCoolClick() {
    console.log(this.state.CoolClicked)
    this.setState({
      CoolClicked: !this.state.CoolClicked
    });

    if(this.state.CoolWarmClicked === true) {
      this.setState({
        CoolWarmClicked: false
      });
    }

    if(this.state.WarmClicked === true) {
      this.setState({
        WarmClicked: false
      });
    }

  }

  handleCoolWarmClick() {
    this.setState({
      CoolWarmClicked: !this.state.CoolWarmClicked
    });

    if(this.state.CoolClicked === true) {
      this.setState({
        CoolClicked: false
      });
    }

    if(this.state.WarmClicked === true) {
      this.setState({
        WarmClicked: false
      });
    }
  }

  handleWarmClick() {
    this.setState({
      WarmClicked: !this.state.WarmClicked
    });

    if(this.state.CoolClicked === true) {
      this.setState({
        CoolClicked: false
      });
    }

    if(this.state.CoolWarmClicked === true) {
      this.setState({
        CoolWarmClicked: false
      });
    }
  }


  handleHighClick() {
    this.setState({
      HighClicked: !this.state.HighClicked
    });

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

    if(this.state.LowClicked === true) {
      this.setState({
        LowClicked: false
      });
    }

  }

handleHighLowClick() {
    this.setState({
      HighLowClicked: !this.state.HighLowClicked
    });

    if(this.state.HighClicked === true) {
      this.setState({
        HighClicked: false
      });
    }

    if(this.state.LowClicked === true) {
      this.setState({
        LowClicked: false
      });
    }

  }

handleLowClick() {
    this.setState({
      LowClicked: !this.state.LowClicked
    });

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

}

handleBrightClick() {
    this.setState({
      BrightClicked: !this.state.BrightClicked
    });

    if(this.state.BrightDarkClicked === true) {
      this.setState({
        BrightDarkClicked: false
      });
    }

    if(this.state.DarkClicked === true) {
      this.setState({
        DarkClicked: false
      });
    }

}


handleBrightDarkClick() {
    this.setState({
      BrightDarkClicked: !this.state.BrightDarkClicked
    });

    if(this.state.BrightClicked === true) {
      this.setState({
        BrightClicked: false
      });
    }

    if(this.state.DarkClicked === true) {
      this.setState({
        DarkClicked: false
      });
    }

}

handleDarkClick() {
    this.setState({
      DarkClicked: !this.state.DarkClicked
    });

    if(this.state.BrightClicked === true) {
      this.setState({
        BrightClicked: false
      });
    }

    if(this.state.BrightDarkClicked === true) {
      this.setState({
        BrightDarkClicked: false
      });
    }

}

checkItem(e, shape) {
  if (shape === 'circle') {
      this.setState ({
        circle: !this.state.circle
      })
  }

  if (shape === 'square') {
    this.setState ({
      square: !this.state.square
    })
  }

  if (shape === 'triangle') {
  this.setState ({
    triangle: !this.state.triangle
  })
  }


}


  render(){

    return <div className="App">

      <header className="leftside">
        <poster/>
        <h2 className='title'> Automatic Background Generator </h2>

        <div className='colshape'> Color Range </div>

        <div className = 'buttonflex'>
          <p className='hsv'> Hue:  </p>

          <div className = 'buttongroup'>

            <button className={this.state.CoolClicked ? "buttonClicked": "buttonUnclicked"} id='left' onClick={this.handleCoolClick}> Cool </button>
            <button className={this.state.CoolWarmClicked ? "buttonClicked": "buttonUnclicked"} onClick={this.handleCoolWarmClick}> Cool & Warm </button>
            <button className={this.state.WarmClicked ? "buttonClicked": "buttonUnclicked"} id='right' onClick={this.handleWarmClick}> Warm </button>

          </div>

        </div>
         <div className = 'buttonflex'>
          <p className='hsv'> Saturation:  </p>

          <div className = 'buttongroup'> 
            <button className={this.state.HighClicked ? "buttonClicked": "buttonUnclicked"} id='left' onClick={this.handleHighClick}> High </button>
            <button className={this.state.HighLowClicked ? "buttonClicked": "buttonUnclicked"} onClick={this.handleHighLowClick}> Low & High </button>
            <button className={this.state.LowClicked ? "buttonClicked": "buttonUnclicked"} id='right' onClick={this.handleLowClick}> High </button>
          </div>

        </div>
         <div className = 'buttonflex'>
          <p className='hsv'> Value:  </p>
          <div className ='buttongroup'>
            <button className={this.state.BrightClicked ? "buttonClicked": "buttonUnclicked"} id='left' onClick={this.handleBrightClick} id='left'> Dark </button>
            <button className={this.state.BrightDarkClicked ? "buttonClicked": "buttonUnclicked"} onClick={this.handleBrightDarkClick}> Dark & Bright</button>
            <button className={this.state.DarkClicked ? "buttonClicked": "buttonUnclicked"} id='right' onClick={this.handleDarkClick}> Bright </button>
          </div>
        </div>


         <div className='colshape'> Shape Preference </div>

        <div className='checkboxes'>


          <input type = 'checkbox' onChange={ (e) => this.checkItem(e, 'circle') } /> 
          <span className="dot"></span>

          <input type = 'checkbox' onChange={ (e) => this.checkItem(e, 'square') } / > 
          <span className="square"></span>

          <input type = 'checkbox' onChange={ (e) => this.checkItem(e, 'triangle') }/> 
          <span className='triangle'> </span>
        </div>

        <button className='genbutton' onClick={()=>{this.props.history.push('/secondpage');}}> Generate > </button>


      </header>

      <header className = 'rightside'>

        <div className='rightsideflex'> 
          <h2 id="samples"> Samples </h2>

          <div className ='row'>
            <Poster />
            <Poster />
          </div>

          <div className ='row'>
          <Poster />
          <Poster />
          </div>

          <div className ='row'>
          <Poster />
          <Poster />
          </div>

          <div className ='row'>
          <Poster />
          <Poster />
          </div>

          <div className ='row'>
          <Poster />
          <Poster />
          </div>

        </div>

      </header>

      <header className = 'evenmorerightside'> 

        <h2>
        Instructions:
        </h2>

        <div>
        1. Click buttons to toggle Color Range and Shape Preferences
        </div> 

        <div>
        2. See Samples
        </div>

        <div>
        2. When satisfied with samples, click the 'Generate' button to generate related posters 
        </div>

      </header>

    </div>
  } 
}

const history = createHashHistory()
export {App, img, history};
