import React, { Component } from "react";
import './mergepage.css';
import MergePoster from './mergeposter.js'
import Poster from './poster.js'
import PosterSample from './postersamples'


const ii1 = require('./img/sample1.png')
const ii2 = require('./img/sample2.png')
const ii3 = require('./img/sample3.png')
const ii4 = require('./img/sample4.png')
const ii5 = require('./img/sample5.png')
const ii6 = require('./img/sample6.png')
const ii7 = require('./img/sample7.png')
const ii8 = require('./img/sample8.png')
const ii9 = require('./img/sample9.png')
const ii10 = require('./img/sample10.png')


class MergePage extends Component {

  constructor(props) {
  	super(props)

  	this.state = {
  		leftrightarray: [],
  		clickedposterleft: leftrightarray[0],
  		clickedposterright: leftrightarray[1]
  	}

  	this.handleCancel = this.handleCancel.bind(this)
  }

// when you click a poster, set the local states correctly based on your selection
  handleSelectionMerge(chosenposter1, chosenposter2) {
  	this.setState({
      clickedposterleft: chosenposter1,
      clickedposterright: chosenposter2
    });


  }

  handleCancel() {
  	this.setState ( {
  		clickedposterleft: null,
  		clickedposterright: null
  		})

  }

	render() {
		return(

      <div className = 'lrcontainer2'>

	      <div className = 'leftside2'>

	      	<div className='buttflexrow'>
		      	<button className='button2' onClick={()=>{this.props.history.push('/secondpage');}} > Back </button>

		      	<div className='instr'> Choose two to see transition! </div>

		      	<button className='button2' onClick={this.handleCancel}> Cancel </button>
	      	</div>

	      	<div className = 'row'>
	      		<Poster importedposter = {ii1} handleSelection={this.handleSelectionMerge}  />
	      		<Poster importedposter = {ii2} handleSelection={this.handleSelectionMerge}  />
	      		<Poster importedposter = {ii3} handleSelection={this.handleSelectionMerge}  />
	      		<Poster importedposter = {ii4} handleSelection={this.handleSelectionMerge}  />

	      	</div>

	      </div>


	      <div className = 'rightside3'>

	      	<div className = 'row3'>
	      		<PosterSample posterselection={this.state.clickedposterleft} />
	      		<span className="square2"></span>
	  			<span className="square2"></span>
	  			<span className="square2"></span>
	  			<span className="square2"></span>
	      		<PosterSample posterselection={this.state.clickedposterright} />
	      	</div>


	      </div>

      </div>



		);
	}
}

export default MergePage;
