import React from 'react';
// import logo from './logo.svg';
import './App.css';

// this was used to test the functionality of exporting things from page to page
import PosterSamples from './postersamples.js'
import { createHashHistory } from 'history'
import axios  from 'axios'

import doggo from './img/doggo.jpg';

const img=10000000000;

const i1 = './img/doggo.jpg'
const i2 = './img/poster1.jpg'
const i3 = './img/poster2.jpg'
const i4 = './img/poster3.jpeg'
const i5 = './img/poster4.jpeg'
const i6 = './img/poster5.jpeg'
const i7 = './img/poster6.jpg'
const i8 = './img/poster7.jpg'
const i9 = './img/poster8.jpeg'
const i10 = './img/poster9.jpeg'


class App extends React.Component{

  constructor(props) {
    super(props);
    this.handleCoolClick = this.handleCoolClick.bind(this)
    this.handleCoolWarmClick = this.handleCoolWarmClick.bind(this)
    this.handleWarmClick = this.handleWarmClick.bind(this)

    this.handleHighClick = this.handleHighClick.bind(this)
    this.handleHighLowClick = this.handleHighLowClick.bind(this)
    this.handleLowClick = this.handleLowClick.bind(this)

    this.handleBrightClick = this.handleBrightClick.bind(this)
    this.handleBrightDarkClick = this.handleBrightDarkClick.bind(this)
    this.handleDarkClick = this.handleDarkClick.bind(this)

    this.handleGenerate = this.handleGenerate.bind(this)

    this.state = {
      CoolClicked: false,
      CoolWarmClicked: true,
      WarmClicked: false,

      HighClicked: false,
      HighLowClicked: true,
      LowClicked: false,

      BrightClicked: false,
      BrightDarkClicked: true,
      DarkClicked: false,

      circle: false,
      square: false,
      triangle: false,

      posters: []
    }
  }

  componentDidMount() {
    this.state.posters.push(i1)
    this.state.posters.push(i2)
    this.state.posters.push(i3)
    this.state.posters.push(i4)
    this.state.posters.push(i5)
    this.state.posters.push(i6)
    this.state.posters.push(i7)
    this.state.posters.push(i8)
    this.state.posters.push(i9)
    this.state.posters.push(i10)

  } 

  componentDidUpdate(prevProps, prevState) {
    console.log(this.state)
  }

  handleCoolClick() {

    this.setState( prevState => ({
      CoolClicked: !prevState.CoolClicked
    }));


    if(this.state.CoolWarmClicked === true) {
      this.setState( prevState => ({
        CoolWarmClicked: !prevState.CoolWarmClicked
      }));
    }

    if(this.state.WarmClicked === true) {
      this.setState( prevState => ({
        WarmClicked: !prevState.WarmClicked
      }));
    }

  }

  handleCoolWarmClick() {


    this.setState({
      CoolWarmClicked: !this.state.CoolWarmClicked
    });

    if(this.state.CoolClicked === true) {
      this.setState({
        CoolClicked: false
      });
    }

    if(this.state.WarmClicked === true) {
      this.setState({
        WarmClicked: false
      });
    }

    console.log(this.state)
  }

  handleWarmClick() {
    this.setState({
      WarmClicked: !this.state.WarmClicked
    });

    if(this.state.CoolClicked === true) {
      this.setState({
        CoolClicked: false
      });
    }

    if(this.state.CoolWarmClicked === true) {
      this.setState({
        CoolWarmClicked: false
      });
    }
  }


  handleHighClick() {
    this.setState({
      HighClicked: !this.state.HighClicked
    });

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

    if(this.state.LowClicked === true) {
      this.setState({
        LowClicked: false
      });
    }

  }

handleHighLowClick() {
    this.setState({
      HighLowClicked: !this.state.HighLowClicked
    });

    if(this.state.HighClicked === true) {
      this.setState({
        HighClicked: false
      });
    }

    if(this.state.LowClicked === true) {
      this.setState({
        LowClicked: false
      });
    }

  }

handleLowClick() {
    this.setState({
      LowClicked: !this.state.LowClicked
    });

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

    if(this.state.HighLowClicked === true) {
      this.setState({
        HighLowClicked: false
      });
    }

}

handleBrightClick() {
    this.setState({
      BrightClicked: !this.state.BrightClicked
    });

    if(this.state.BrightDarkClicked === true) {
      this.setState({
        BrightDarkClicked: false
      });
    }

    if(this.state.DarkClicked === true) {
      this.setState({
        DarkClicked: false
      });
    }

}


handleBrightDarkClick() {
    this.setState({
      BrightDarkClicked: !this.state.BrightDarkClicked
    });

    if(this.state.BrightClicked === true) {
      this.setState({
        BrightClicked: false
      });
    }

    if(this.state.DarkClicked === true) {
      this.setState({
        DarkClicked: false
      });
    }

}

handleDarkClick() {
    this.setState({
      DarkClicked: !this.state.DarkClicked
    });

    if(this.state.BrightClicked === true) {
      this.setState({
        BrightClicked: false
      });
    }

    if(this.state.BrightDarkClicked === true) {
      this.setState({
        BrightDarkClicked: false
      });
    }

}

checkItem(e, shape) {
  if (shape === 'circle') {
      this.setState ({
        circle: !this.state.circle
      })
  }

  if (shape === 'square') {
    this.setState ({
      square: !this.state.square
    })
  }

  if (shape === 'triangle') {
  this.setState ({
    triangle: !this.state.triangle
  })
  }


}

check() {
  console.log(this.state)
}

handleGenerate() {
  this.props.history.push('/secondpage');

}


  render(){

    return <div className="App">

      <header className="leftside">
